#include <iostream>
#include <string>
#include <vector>
#include <set>
#include <unordered_map>

using namespace std;

int numSplits(string s) {
    int count = 0;
    int ret = 0;
    unordered_map<char, int> rMap;
    
    for(auto st : s) {
        rMap[st]++;
    }
    unordered_map<char, int> lMap;
    int i = 0;
    while(count < s.length())
    {
        char ch = s[i];
        lMap[ch]++;
        rMap[ch]--;
        if(rMap[ch] == 0) {
            rMap.erase(ch);
        }
        if(lMap.size() == rMap.size()) {
            ret++;
        }
        ++i;
        ++count;
    }
    
    return ret;
}

int main(void)
{
    string input = "";
    
    cout << numSplits(input) << endl;
    
    return 0;
}
